class Student_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get student by student_id
     */
    function get_student($student_id)
    {
        return $this->db->get_where('students',array('student_id'=>$student_id))->row_array();
    }
        
    /*
     * Get all students
     */
    function get_all_students()
    {
        $this->db->order_by('student_id', 'desc');
        return $this->db->get('students')->result_array();
    }
        
    /*
     * function to add new student
     */
    function add_student($params)
    {
        $this->db->insert('students',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update student
     */
    function update_student($student_id,$params)
    {
        $this->db->where('student_id',$student_id);
        return $this->db->update('students',$params);
    }
    
    /*
     * function to delete student
     */
    function delete_student($student_id)
    {
        return $this->db->delete('students',array('student_id'=>$student_id));
    }
}
